def getClassTable(filename='curriculum.txt'):
    classTable.clear()
    for i in range(14):
        classTable.append(["","","","",""])
    with open(filename, 'r', encoding='UTF-8') as f:
        count = 0
        for line in f:
            if count%16!=0 and count%16!=15:
                t = line
                ts = t.split()
                for i in range(5):
                    if ts[i+1] != "无":
                        classTable[(count-1)%16][i] = classTable[(count-1)%16][i] + ts[i+1]
                        if count<48:
                            classTable[(count-1)%16][i]+= "\n"
